contracts/ENSProfileContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * ENSProfileContract - ENS User Profile Management
 * Handles user profile creation and management associated with ENS names
 * @author MorphCash Team
 */
contract ENSProfileContract is Ownable, ReentrancyGuard {
    // ENS User Profile Struct
    struct UserProfile {
        string displayName;
        string bio;
        string avatar;
        string website;
        string twitter;
        string github;
        string discord;
        string telegram;
        bool isActive;
        uint256 createdAt;
        uint256 updatedAt;
    }

    // State Variables
    address public immutable contractOwner;
    
    // Mappings
    mapping(bytes32 => UserProfile) public userProfiles;
    mapping(address => bytes32) public addressToENS;
    
    // Arrays
    bytes32[] public registeredENS;

    // Events
    event ProfileCreated(bytes32 indexed ensHash, address indexed user, string ensName);
    event ProfileUpdated(bytes32 indexed ensHash, address indexed user, string ensName);

    // Constructor
    constructor(address _owner) {
        contractOwner = _owner;
        _transferOwnership(_owner);
    }

    // Modifier
    modifier isOwner() {
        require(msg.sender == contractOwner, "Not the Owner");
        _;
    }

    /**
     * @dev Create a new user profile associated with an ENS name
     * @param ensHash The keccak256 hash of the ENS name
     * @param ensName The actual ENS name string for events
     * @param profile The initial profile data
     */
    function createProfile(
        bytes32 ensHash,
        string memory ensName,
        UserProfile memory profile
    ) external nonReentrant {
        require(userProfiles[ensHash].createdAt == 0, "Profile already exists");
        require(addressToENS[msg.sender] == bytes32(0), "Address already has a profile");
        
        profile.createdAt = block.timestamp;
        profile.updatedAt = block.timestamp;
        profile.isActive = true;
        
        userProfiles[ensHash] = profile;
        addressToENS[msg.sender] = ensHash;
        registeredENS.push(ensHash);
        
        emit ProfileCreated(ensHash, msg.sender, ensName);
    }

    /**
     * @dev Update an existing user profile
     * @param ensHash The keccak256 hash of the ENS name
     * @param ensName The actual ENS name string for events
     * @param profile The updated profile data
     */
    function updateProfile(
        bytes32 ensHash,
        string memory ensName,
        UserProfile memory profile
    ) external nonReentrant {
        require(userProfiles[ensHash].createdAt > 0, "Profile does not exist");
        require(addressToENS[msg.sender] == ensHash, "Not the profile owner");
        
        profile.createdAt = userProfiles[ensHash].createdAt;
        profile.updatedAt = block.timestamp;
        profile.isActive = true;
        
        userProfiles[ensHash] = profile;
        
        emit ProfileUpdated(ensHash, msg.sender, ensName);
    }

    /**
     * @dev Get user profile by ENS name hash
     * @param ensHash The keccak256 hash of the ENS name
     * @return The user profile data
     */
    function getProfile(bytes32 ensHash) external view returns (UserProfile memory) {
        require(userProfiles[ensHash].createdAt > 0, "Profile does not exist");
        return userProfiles[ensHash];
    }

    /**
     * @dev Get user profile by address
     * @param userAddress The address of the user
     * @return The user profile data
     */
    function getProfileByAddress(address userAddress) external view returns (UserProfile memory) {
        bytes32 ensHash = addressToENS[userAddress];
        require(ensHash != bytes32(0), "No profile found for this address");
        return userProfiles[ensHash];
    }

    /**
     * @dev Get total number of registered profiles
     * @return The number of registered profiles
     */
    function getTotalProfiles() external view returns (uint256) {
        return registeredENS.length;
    }

    /**
     * @dev Get all registered ENS hashes
     * @return Array of registered ENS hashes
     */
    function getAllRegisteredENS() external view returns (bytes32[] memory) {
        return registeredENS;
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

contracts/GreetingContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// Useful for debugging. Remove when deploying to a live network.
import "hardhat/console.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * GreetingContract - Basic Greeting Functionality
 * Handles greeting messages and basic counter functionality
 * @author MorphCash Team
 */
contract GreetingContract is Ownable, ReentrancyGuard {
    // State Variables
    address public immutable contractOwner;
    string public greeting = "Building Unstoppable Apps!!!";
    bool public premium = false;
    uint256 public totalCounter = 0;
    mapping(address => uint) public userGreetingCounter;

    // Events
    event GreetingChange(address indexed greetingSetter, string newGreeting, bool premium, uint256 value);

    // Constructor
    constructor(address _owner) {
        contractOwner = _owner;
        _transferOwnership(_owner);
    }

    // Modifier
    modifier isOwner() {
        require(msg.sender == contractOwner, "Not the Owner");
        _;
    }

    /**
     * Function that allows anyone to change the state variable "greeting" of the contract and increase the counters
     *
     * @param _newGreeting (string memory) - new greeting to save on the contract
     */
    function setGreeting(string memory _newGreeting) public payable {
        // Print data to the hardhat chain console. Remove when deploying to a live network.
        console.log("Setting new greeting '%s' from %s", _newGreeting, msg.sender);

        // Change state variables
        greeting = _newGreeting;
        totalCounter += 1;
        userGreetingCounter[msg.sender] += 1;

        // msg.value: built-in global variable that represents the amount of ether sent with the transaction
        if (msg.value > 0) {
            premium = true;
        } else {
            premium = false;
        }

        // emit: keyword used to trigger an event
        emit GreetingChange(msg.sender, _newGreeting, msg.value > 0, msg.value);
    }

    /**
     * Function that allows the owner to withdraw all the Ether in the contract
     * The function can only be called by the owner of the contract as defined by the isOwner modifier
     */
    function withdraw() public isOwner {
        (bool success, ) = contractOwner.call{ value: address(this).balance }("");
        require(success, "Failed to send Ether");
    }

    /**
     * @dev Get the current greeting
     * @return The current greeting string
     */
    function getGreeting() external view returns (string memory) {
        return greeting;
    }

    /**
     * @dev Get the total counter value
     * @return The total counter value
     */
    function getTotalCounter() external view returns (uint256) {
        return totalCounter;
    }

    /**
     * @dev Get the greeting counter for a specific user
     * @param user The user's address
     * @return The user's greeting counter
     */
    function getUserGreetingCounter(address user) external view returns (uint256) {
        return userGreetingCounter[user];
    }

    /**
     * @dev Check if premium mode is active
     * @return True if premium mode is active
     */
    function isPremium() external view returns (bool) {
        return premium;
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

contracts/PaymentContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * PaymentContract - Payment Verification and Processing
 * Handles payment creation, verification, and processing
 * @author MorphCash Team
 */
contract PaymentContract is Ownable, ReentrancyGuard {
    // Payment Struct
    struct Payment {
        uint256 paymentId;
        address user;
        uint256 amount; // Amount in wei (ETH)
        uint256 ghsAmount; // Amount in GHS (for reference)
        string paymentMethod; // "mobile_money" or "crypto"
        string paymentReference;
        string transactionId;
        bool isVerified;
        bool isProcessed;
        uint256 createdAt;
        uint256 verifiedAt;
    }

    // State Variables
    address public immutable contractOwner;
    
    // Mappings
    mapping(uint256 => Payment) public payments;
    mapping(string => uint256) public paymentReferenceToPaymentId;
    mapping(address => uint256[]) public userPayments;
    
    // Arrays
    uint256 public nextPaymentId = 1;

    // Events
    event PaymentCreated(uint256 indexed paymentId, address indexed user, uint256 amount, string paymentReference);
    event PaymentVerified(uint256 indexed paymentId, address indexed user, string transactionId);
    event PaymentProcessed(uint256 indexed paymentId, address indexed user);

    // Constructor
    constructor(address _owner) {
        contractOwner = _owner;
        _transferOwnership(_owner);
    }

    // Modifier
    modifier isOwner() {
        require(msg.sender == contractOwner, "Not the Owner");
        _;
    }

    /**
     * @dev Create a payment record for verification
     * @param amount Amount in wei (ETH)
     * @param ghsAmount Amount in GHS (for reference)
     * @param paymentMethod Payment method ("mobile_money" or "crypto")
     * @param paymentReference Payment reference
     * @return paymentId The created payment ID
     */
    function createPayment(
        uint256 amount,
        uint256 ghsAmount,
        string memory paymentMethod,
        string memory paymentReference
    ) external nonReentrant returns (uint256) {
        require(amount > 0, "Amount must be greater than 0");
        require(bytes(paymentReference).length > 0, "Reference cannot be empty");
        require(paymentReferenceToPaymentId[paymentReference] == 0, "Reference already exists");
        
        uint256 paymentId = nextPaymentId++;
        
        Payment memory newPayment = Payment({
            paymentId: paymentId,
            user: msg.sender,
            amount: amount,
            ghsAmount: ghsAmount,
            paymentMethod: paymentMethod,
            paymentReference: paymentReference,
            transactionId: "",
            isVerified: false,
            isProcessed: false,
            createdAt: block.timestamp,
            verifiedAt: 0
        });
        
        payments[paymentId] = newPayment;
        paymentReferenceToPaymentId[paymentReference] = paymentId;
        userPayments[msg.sender].push(paymentId);
        
        emit PaymentCreated(paymentId, msg.sender, amount, paymentReference);
        
        return paymentId;
    }

    /**
     * @dev Verify a payment (called by backend after payment confirmation)
     * @param paymentId The payment ID to verify
     * @param transactionId The transaction ID from payment provider
     */
    function verifyPayment(
        uint256 paymentId,
        string memory transactionId
    ) external isOwner {
        require(payments[paymentId].paymentId != 0, "Payment does not exist");
        require(!payments[paymentId].isVerified, "Payment already verified");
        require(bytes(transactionId).length > 0, "Transaction ID cannot be empty");
        
        payments[paymentId].isVerified = true;
        payments[paymentId].transactionId = transactionId;
        payments[paymentId].verifiedAt = block.timestamp;
        
        emit PaymentVerified(paymentId, payments[paymentId].user, transactionId);
    }

    /**
     * @dev Mark a payment as processed
     * @param paymentId The payment ID to process
     */
    function processPayment(uint256 paymentId) external isOwner {
        require(payments[paymentId].paymentId != 0, "Payment does not exist");
        require(payments[paymentId].isVerified, "Payment not verified");
        require(!payments[paymentId].isProcessed, "Payment already processed");
        
        payments[paymentId].isProcessed = true;
        
        emit PaymentProcessed(paymentId, payments[paymentId].user);
    }

    /**
     * @dev Get payment details by ID
     * @param paymentId The payment ID
     * @return The payment data
     */
    function getPayment(uint256 paymentId) external view returns (Payment memory) {
        require(payments[paymentId].paymentId != 0, "Payment does not exist");
        return payments[paymentId];
    }

    /**
     * @dev Get payment by reference
     * @param paymentReference The payment reference
     * @return The payment data
     */
    function getPaymentByReference(string memory paymentReference) external view returns (Payment memory) {
        uint256 paymentId = paymentReferenceToPaymentId[paymentReference];
        require(paymentId != 0, "Payment not found");
        return payments[paymentId];
    }

    /**
     * @dev Get all payments for a user
     * @param userAddress The user's address
     * @return Array of payment IDs
     */
    function getUserPayments(address userAddress) external view returns (uint256[] memory) {
        return userPayments[userAddress];
    }

    /**
     * @dev Check if a payment is verified
     * @param paymentId The payment ID
     * @return True if verified, false otherwise
     */
    function isPaymentVerified(uint256 paymentId) external view returns (bool) {
        require(payments[paymentId].paymentId != 0, "Payment does not exist");
        return payments[paymentId].isVerified;
    }

    /**
     * @dev Check if a payment is processed
     * @param paymentId The payment ID
     * @return True if processed, false otherwise
     */
    function isPaymentProcessed(uint256 paymentId) external view returns (bool) {
        require(payments[paymentId].paymentId != 0, "Payment does not exist");
        return payments[paymentId].isProcessed;
    }

    /**
     * @dev Get total number of payments
     * @return The total number of payments
     */
    function getTotalPayments() external view returns (uint256) {
        return nextPaymentId - 1;
    }

    /**
     * @dev Get payments by status
     * @param isVerified Whether to filter by verified status
     * @param isProcessed Whether to filter by processed status
     * @return Array of payment IDs matching the criteria
     */
    function getPaymentsByStatus(bool isVerified, bool isProcessed) external view returns (uint256[] memory) {
        uint256[] memory matchingPayments = new uint256[](nextPaymentId - 1);
        uint256 count = 0;
        
        for (uint256 i = 1; i < nextPaymentId; i++) {
            if (payments[i].isVerified == isVerified && payments[i].isProcessed == isProcessed) {
                matchingPayments[count] = i;
                count++;
            }
        }
        
        // Resize array to actual count
        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = matchingPayments[i];
        }
        
        return result;
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

contracts/VirtualCardContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * VirtualCardContract - Virtual Card Management
 * Handles virtual card creation, updating, and management
 * @author MorphCash Team
 */
contract VirtualCardContract is Ownable, ReentrancyGuard {
    // Virtual Card Struct
    struct VirtualCard {
        uint256 cardId;
        string cardName;
        string cardNumber; // Masked: ****1234
        string expiryDate;
        string cardType; // Visa, Mastercard, etc.
        uint256 spendingLimit;
        uint256 currentSpend;
        bool isActive;
        uint256 createdAt;
    }

    // State Variables
    address public immutable contractOwner;
    
    // Mappings
    mapping(address => VirtualCard[]) public userVirtualCards;
    mapping(address => uint256) public userCardCount;
    
    // Arrays
    uint256 public nextCardId = 1;

    // Events
    event VirtualCardCreated(address indexed user, uint256 indexed cardId, string cardName);
    event VirtualCardUpdated(address indexed user, uint256 indexed cardId);
    event VirtualCardDeactivated(address indexed user, uint256 indexed cardId);

    // Constructor
    constructor(address _owner) {
        contractOwner = _owner;
        _transferOwnership(_owner);
    }

    // Modifier
    modifier isOwner() {
        require(msg.sender == contractOwner, "Not the Owner");
        _;
    }

    // Helper function to convert uint to string
    function uint2str(uint256 _i) internal pure returns (string memory) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k-1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    /**
     * @dev Create a new virtual card for the user
     * @param cardName Name for the virtual card
     * @param cardNumber Masked card number (e.g., ****1234)
     * @param cardType Type of card (Visa, Mastercard, etc.)
     * @param spendingLimit Maximum spending limit for the card
     */
    function createVirtualCard(
        string memory cardName,
        string memory cardNumber,
        string memory cardType,
        uint256 spendingLimit
    ) external nonReentrant {
        uint256 cardId = nextCardId++;
        
        // Generate expiry date (3 years from now)
        uint256 currentTime = block.timestamp;
        uint256 expiryTimestamp = currentTime + (3 * 365 * 24 * 60 * 60); // 3 years in seconds
        
        // Convert to MM/YY format
        uint256 expiryYear = (expiryTimestamp / (365 * 24 * 60 * 60)) + 1970;
        uint256 expiryMonth = ((expiryTimestamp % (365 * 24 * 60 * 60)) / (30 * 24 * 60 * 60)) + 1;
        
        string memory expiryDate = string(abi.encodePacked(
            expiryMonth < 10 ? "0" : "",
            uint2str(expiryMonth),
            "/",
            uint2str(expiryYear % 100)
        ));
        
        VirtualCard memory newCard = VirtualCard({
            cardId: cardId,
            cardName: cardName,
            cardNumber: cardNumber,
            expiryDate: expiryDate,
            cardType: cardType,
            spendingLimit: spendingLimit,
            currentSpend: 0,
            isActive: true,
            createdAt: block.timestamp
        });
        
        userVirtualCards[msg.sender].push(newCard);
        userCardCount[msg.sender]++;
        
        emit VirtualCardCreated(msg.sender, cardId, cardName);
    }

    /**
     * @dev Create a virtual card for a specific user (owner only)
     * @param user The address of the user
     * @param cardName Name for the virtual card
     * @param cardNumber Masked card number (e.g., ****1234)
     * @param cardType Type of card (Visa, Mastercard, etc.)
     * @param spendingLimit Maximum spending limit for the card
     */
    function createVirtualCardForUser(
        address user,
        string memory cardName,
        string memory cardNumber,
        string memory cardType,
        uint256 spendingLimit
    ) external isOwner {
        uint256 cardId = nextCardId++;
        
        // Generate expiry date (3 years from now)
        uint256 currentTime = block.timestamp;
        uint256 expiryTimestamp = currentTime + (3 * 365 * 24 * 60 * 60); // 3 years in seconds
        
        // Convert to MM/YY format
        uint256 expiryYear = (expiryTimestamp / (365 * 24 * 60 * 60)) + 1970;
        uint256 expiryMonth = ((expiryTimestamp % (365 * 24 * 60 * 60)) / (30 * 24 * 60 * 60)) + 1;
        
        string memory expiryDate = string(abi.encodePacked(
            expiryMonth < 10 ? "0" : "",
            uint2str(expiryMonth),
            "/",
            uint2str(expiryYear % 100)
        ));
        
        VirtualCard memory newCard = VirtualCard({
            cardId: cardId,
            cardName: cardName,
            cardNumber: cardNumber,
            expiryDate: expiryDate,
            cardType: cardType,
            spendingLimit: spendingLimit,
            currentSpend: 0,
            isActive: true,
            createdAt: block.timestamp
        });
        
        userVirtualCards[user].push(newCard);
        userCardCount[user]++;
        
        emit VirtualCardCreated(user, cardId, cardName);
    }

    /**
     * @dev Update a virtual card
     * @param cardIndex Index of the card in user's card array
     * @param cardName New name for the card
     * @param spendingLimit New spending limit
     */
    function updateVirtualCard(
        uint256 cardIndex,
        string memory cardName,
        uint256 spendingLimit
    ) external nonReentrant {
        require(cardIndex < userVirtualCards[msg.sender].length, "Card does not exist");
        require(userVirtualCards[msg.sender][cardIndex].isActive, "Card is not active");
        
        userVirtualCards[msg.sender][cardIndex].cardName = cardName;
        userVirtualCards[msg.sender][cardIndex].spendingLimit = spendingLimit;
        
        emit VirtualCardUpdated(msg.sender, userVirtualCards[msg.sender][cardIndex].cardId);
    }

    /**
     * @dev Deactivate a virtual card
     * @param cardIndex Index of the card in user's card array
     */
    function deactivateVirtualCard(uint256 cardIndex) external nonReentrant {
        require(cardIndex < userVirtualCards[msg.sender].length, "Card does not exist");
        require(userVirtualCards[msg.sender][cardIndex].isActive, "Card is already inactive");
        
        userVirtualCards[msg.sender][cardIndex].isActive = false;
        
        emit VirtualCardDeactivated(msg.sender, userVirtualCards[msg.sender][cardIndex].cardId);
    }

    /**
     * @dev Get all virtual cards for a user
     * @param userAddress The address of the user
     * @return Array of virtual cards
     */
    function getUserVirtualCards(address userAddress) external view returns (VirtualCard[] memory) {
        return userVirtualCards[userAddress];
    }

    /**
     * @dev Get the number of virtual cards for a user
     * @param userAddress The address of the user
     * @return Number of cards
     */
    function getUserCardCount(address userAddress) external view returns (uint256) {
        return userCardCount[userAddress];
    }

    /**
     * @dev Get a specific virtual card by user and index
     * @param userAddress The address of the user
     * @param cardIndex The index of the card
     * @return The virtual card data
     */
    function getVirtualCard(address userAddress, uint256 cardIndex) external view returns (VirtualCard memory) {
        require(cardIndex < userVirtualCards[userAddress].length, "Card does not exist");
        return userVirtualCards[userAddress][cardIndex];
    }

    /**
     * @dev Get total number of cards created
     * @return The total number of cards
     */
    function getTotalCards() external view returns (uint256) {
        return nextCardId - 1;
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

